class Solution {
public:
    string removeStars(string s) {
        stack<int> st;
        for(int i = 0; i < s.length(); i++){
            if(s[i] =='*'){
                if(!st.empty())st.pop();
            }else{
                st.push(i);
            }
        }
        string ans;
        while(!st.empty()){
            ans.push_back(s[st.top()]);
            st.pop();
        }
        reverse(ans.begin(), ans.end());
        return ans;
    }
};
