class Solution {
public:
    int helper2(vector<vector<int>> &adj, vector<vector<int>> &revAdj, 
    vector<int> &vis, vector<bool> &reach, int curr){
        vis[curr] = 1;
        int ans = 0;
        for(auto i : revAdj[curr]){
            reach[i] = true;
            if(!vis[i])
                ans += helper2(adj, revAdj, vis, reach, i);
        }
        for(auto i : adj[curr]){
            if(!reach[i]){
                ans++;
                reach[i] = true;
            }
            if(!vis[i])
                ans += helper2(adj, revAdj, vis, reach, i);
        }

        return ans;
    }
    int minReorder(int n, vector<vector<int>>& connections) {
        vector<vector<int>> adj(n + 1), revAdj(n + 1);
        for(auto i : connections){
            adj[i[0]].push_back(i[1]);
            revAdj[i[1]].push_back(i[0]);
        }
        vector<bool> reach(n + 1, false);
        reach[0] = true;
        vector<int> vis(n + 1, 0);
        return helper2(adj, revAdj, vis, reach, 0);
    }
};
