class Solution {
public:
    bool reorderedPowerOf2(int n) {
        string s= to_string(n);
        sort(s.begin(), s.end());
        int num = 1;
        for(int i = 1; i < 31; i ++){
            string temp = to_string(num);
            sort(temp.begin(), temp.end());
            if(temp == s){
                return true;
            }
            num *= 2;
        }
        return false;
    }
};
