class Solution {
public:
    vector<string> findRepeatedDnaSequences(string s) {
        int i = 0, j = 10;
        string temp = "";
        for(int i = 0; i < 10; i++){
            temp.push_back(s[i]);
        }
        unordered_map<string, int> umap;
        umap[temp]++;
        while(j < s.length()){
            temp.push_back(s[j]);
            temp.erase(temp.begin());
            umap[temp]++;
            j++;
            i++;
        }
        vector<string> ans;
        for(auto i : umap){
            if(i.second > 1){
                ans.push_back(i.first);
            }
        }
        return ans;
    }
};
