class Solution {
public:
    vector<string> restoreIpAddresses(string s) {
        set<string> ans;
        for(int i = 1;i<=3;i++){
            for(int j = 1;j<=3;j++){
                for(int k = 1;k<=3;k++){
                    if(i + j + k >= s.length())continue;
                    string a = s.substr(0, i);
                    string b = s.substr(i, j);
                    string c = s.substr(i + j, k);
                    string d = s.substr(i + j + k, s.length() - i  -j-k);
                    if(a.length() > 3 ||b.length() > 3 ||c.length() > 3 || d.length() > 3)continue;
                    int A = stoi(a);
                    int B = stoi(b);
                    int C = stoi(c);
                    int D = stoi(d);
                    if(A <=255 && B<=255 && C<=255 && D<=255){
                        string temp =to_string(A) +'.'+to_string(B) +'.'+ to_string(C) + '.' + to_string(D);
                        if(temp.length() == s.length() + 3)
                            ans.insert(temp);
                    }         
                }
            }
        }
        vector<string> st;
        for(auto i : ans){
            st.push_back(i);
        }
        return st;
    }
};
