long dp[100005];
const int MOD = 1e9 + 7;
class Solution {
public:
    long helper(string &s, int &k, int ind){
        if(ind == s.length())return 1;
        if(s[ind] == '0')return 0;
        if(dp[ind] != -1)return dp[ind];
        long long int num = 0, ans = 0;
        for(int i = ind; i < s.length(); i++){
            num = num * 10 + (s[i] - '0');
            if(num > k)break;
            ans += helper(s, k, i + 1);
            ans%=MOD;
        }
        return dp[ind] = ans%MOD;
    }
    int numberOfArrays(string s, int k) {
        memset(dp, -1, sizeof dp);
        return helper(s, k ,0)%MOD;
    }
};
