class Solution {
public:
    vector<int> deckRevealedIncreasing(vector<int>& d) {
       int n = d.size();
        queue<int> q;

        for(int i=0;i<n;i++) q.push(i);
        sort(d.begin(), d.end());
        vector<int> ans(n);

        int i = 0;
        while(!q.empty() and i < n) {
            ans[q.front()] = d[i];

            q.pop();

            q.push(q.front());
            q.pop();
            i++;
        }   
        return  ans;


    }
};
