class Solution {
public:
    int reverse(int x) {

        long int y = 1, y2 = -1;
        for(int i=1;i<=31;i++){
            y *= 2;
            y2 *=2;
        }


        int temp = abs(x);
        long int rev = 0;
        while(temp > 0){

            try{
                rev = rev * 10 + temp%10;
            }
            catch(...){
                return 0;
            }

            temp/=10;
        }

        if(x== 0){
            return 0;
        }
        else{
            rev *= x/(abs(x));
        }
        if(rev > y-1 || rev < y2){
            return 0;
        }else{
            return rev;
        }


    }
};
