/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
ListNode *reverseKGroup(ListNode *head, int k)
{
    vector<ListNode *> arr;
    ListNode *temp = head;
    while (temp != NULL)
    {
        arr.push_back(temp);
        temp = temp->next;
    }
    for (int i = 0; i < (arr.size() / k) * k; i += k)
    {
        reverse(arr.begin() + i, arr.begin() + i + k);
    }
    ListNode *ans = NULL;
    temp = NULL;
    for (int i = 0; i < arr.size(); i++)
    {
        if (ans == NULL)
        {
            ans = arr[i];
            temp = arr[i];
        }
        else
        {
            temp->next = arr[i];
            temp = temp->next;
            temp->next = NULL;
        }
    }

    return ans;
}
};
