class Solution {
public:
    string reverseOnlyLetters(string s) {
        int n =s.length();
        int start = 0;
        int end = n-1;
        while(start<=end){
            if((s[start]>='a' && s[start]<='z' || s[start]>='A' && s[start]<='Z') &&
            (s[end]>='a' && s[end]<='z' || s[end]>='A' && s[end]<='Z')){
                swap(s[start] , s[end]);
                start++;
                end--;
            }else{
                if(s[start]>='a' && s[start]<='z' || s[start]>='A' && s[start]<='Z'){
                    end--;
                }else{
                    start++;
                }
            }
        }
        return s;
    }
};
