class Solution {
public:
    string reverseParentheses(string s) {
        string ans;
        int n = s.length();
        vector<int> v;
        for(int i =0;i<n;i++)  {
            if(s[i] == '(') {
                v.push_back(ans.length());
            }
            else if(s[i] == ')') {
                int j  = v.back();
                v.pop_back();
                reverse(ans.begin() + j, ans.end());
            }

            else {
                ans +=  s[i];

            }
        }
        return ans;
    }

};
