class Solution {
public:
    string reverseVowels(string s) {
        string t = s;
        vector<int> arr;
        for(int i = 0; i < s.length();i++){
            if(s[i] == 'a' || s[i] == 'e' || s[i] == 'i' || s[i] == 'o' || s[i] == 'u'||
            s[i] == 'A' || s[i] == 'E' || s[i] == 'I' || s[i] == 'O' || s[i] == 'U'){
                arr.push_back(i);
            }
        }
        reverse(arr.begin(), arr.end());
        for(int i = 0; i < arr.size();i++){
            t[arr[i]] = s[arr[arr.size() - i - 1]];
        }
        return t;
    }
};
