class Solution {
public:
    static bool cmp(pair<int,int> x,pair<int,int> y){
        if(x.first==y.first){
            return x.second<y.second;
        }
        return x.first>y.first;
    }
    vector<int> topStudents(vector<string>& p, vector<string>& n, vector<string>& report, vector<int>& sid, int k) {
        vector<pair<int,int>> vec;
        unordered_map<string,int> pos,neg;
        for(auto i:p) pos[i]++;
        for(auto i:n) neg[i]++;

        int idx=0;
        for(auto i:report){
            string s;
            int cnt=0;
            for(auto c:i){
                if(c==' '&&s!=""){
                    if(pos.count(s)){
                        cnt+=3;
                    }else if(neg.count(s)){
                        cnt--;
                    }
                    s="";
                    continue;
                }
                s.push_back(c);
            }
            if(s!=""&&s!=" "){
                    if(pos.count(s)){
                        cnt+=3;
                    }else if(neg.count(s)){
                        cnt--;
                    }
                    s="";
            }
            vec.push_back({cnt,sid[idx++]});
        }
        sort(begin(vec),end(vec),cmp);

        vector<int> ans;
        for(auto i:vec){
            // cout<<i.first<<" "<<i.second;
            if(!k)break;
            ans.push_back(i.second);
            k--;
        }
        return ans;


    }
};
