class Solution {
public:
    int getPriority(char c){
        if(c == 'I'){
            return 1;
        }else if(c == 'V'){
            return 5;
        }else if(c == 'X'){
            return 10;
        }else if(c == 'L'){
            return 50;
        }else if(c == 'C'){
            return 100;
        }else if(c == 'D'){
            return 500;
        }else if(c == 'M'){
            return 1000;
        }
        return 0;
    }
    int romanToInt(string s) {
        char prev = ' ';
        int ans = 0;
        for(int i = s.length() - 1; i >= 0 ; i --){
            if(getPriority(s[i]) >= getPriority(prev)){
                ans += getPriority(s[i]);
            }else{
                ans -= getPriority(s[i]);
            }
            prev = s[i];
        }
        return ans;
    }
};
