class Solution {
public:
int search(vector<int> &arr, int target)
{
    int lo = 0, hi = arr.size() - 1;
    while (lo <= hi)
    {
        int mid = (hi + lo) / 2;
        if (target == arr[mid])
            return mid;
        if (arr[lo] <= arr[mid])
        {
            if (target >= arr[lo] && target <= arr[mid])
            {
                hi = mid - 1;
            }
            else
            {
                lo = mid + 1;
            }
        }
        else
        {
            if (target >= arr[mid] && target <= arr[hi])
            {
                lo = mid + 1;
            }
            else
            {
                hi = mid - 1;
            }
        }
    }
    return -1;
}
};
