class Solution {
public:
    int secondHighest(string s) {
        set<int> st;
        for(auto i : s){
            if(i >='0' && i <='9'){
                st.insert(i - '0');
            }
        }
        vector<int> arr;
        for(auto i : st)arr.push_back(i);
        if(arr.size() >= 2){
            return arr[arr.size() - 2];
        }
        return -1;
    }
};
