class Solution:
    def semiOrderedPermutation(self, nums: List[int]) -> int:
        x, y, n = -1, -1, len(nums)
        for i in range(0, n):
            if(nums[i] == 1):
                x = i
            if(nums[i] == n):
                y = i
        if(x < y):
            return x + n - y - 1
        else:
            return x + n - (y + 1) - 1
