class Solution {
public:
    string largestWordCount(vector<string>& messages, vector<string>& senders) {
        unordered_map<string,int> umap;
        for(int i=0;i<messages.size();i++){
            int spc=0;
            for(auto iter:messages[i]){
                if(iter==' '){
                    spc++;
                }
            }
            if(messages[i].size()>0) spc++;
            umap[senders[i]]+=spc;
            // cout<<spc<<" ";
        }
        // cout<<endl;

        vector<string> ans;
        int mx=0;
        for(auto i:umap){
            mx=max(mx,i.second);
        }
        // cout<<umap["Alice"]<<" ";
        for(auto i:umap){
           if(i.second==mx){
               ans.push_back(i.first);
           }
        }
        sort(ans.begin(),ans.end());
        return ans.back();


    }
};
