class Solution {
public:
    vector<int> findErrorNums(vector<int>& nums) {
        vector<int> arr(nums.size() + 1, 0);
        for(auto i : nums)arr[i]++;
        int ans1 = 0, ans2 = 0;
        for(int i = 1; i <= nums.size();i++){
            if(arr[i] == 2)ans1 = i;
            if(arr[i] == 0)ans2 = i;
        }
        return {ans1, ans2};
    }
};
