class Solution {
public:
    vector<vector<int>> shiftGrid(vector<vector<int>>& grid, int k) {
        int n = grid.size();
        int m = grid[0].size();
        while(k--) {
            int temp = grid[n-1][m-1];
             for(int i = n - 1; i >= 0; i--) {

                 for(int j = m - 1; j >= 0; j--) {
                     if(i == 0 and j == 0) {
                         grid[i][j] = temp;
                         continue;
                     }

                    if(j - 1 < 0) {
                         grid[i][j] = grid[i-1][m-1];
                    }
                     else {
                         grid[i][j] = grid[i][j-1];
                     }
                 }
             }
        }
        return grid;
    }
};
