class Solution {
public:
    static bool comp(string &s, string &t) 
    {
        return s.size() > t.size();
    }
    int minimumLengthEncoding(vector<string>& words) {
        sort(words.begin(), words.end(), comp);
        string ans;
        map<string, bool> vis;
        for(auto i : words) {
            if(vis[i] == true) continue;
            ans += i;
            ans.push_back('#');
            for(int j = 0; j < i.size(); j++) {
                vis[i.substr(j)] = true;
            }
        }
        return (int) ans.size();

    }
};
