class Solution {
public:

    string shortestCompletingWord(string licensePlate, vector<string>& words) {
        int n= words.size();
        vector<vector<int>> arr(n, vector<int>(26, 0));
        for(int i = 0; i < n;i++){
            for(auto j : words[i]){
                arr[i][j-'a']++;
            }
        }
        vector<int> mat(26, 0);
        for(auto i : licensePlate){
            if(i>='a' && i <='z'){
                mat[i-'a']++;
            }else if(i>='A' && i <='Z'){
                mat[i-'A']++;
            }
        }
        string ans = "1234567890123456";
        for(int i = 0; i < n;i++){
            int f = 0;
            for(int j = 0; j < 26;j++){
                if(mat[j] == 0)continue;
                if(mat[j] > arr[i][j]){
                    f = 1;
                    break;
                }
            }
            if(f == 0){
                if(words[i].length() < ans.length()){
                    ans = words[i];
                }
            }
        }
        return ans;
    }
};
