class Solution {
public:
    int shortestSequence(vector<int>& nums, int k) {
        int ans = 1;
        set<int> st;
        for (int& i : nums) {
            st.insert(i);
            if (st.size() == k) {
                ans++;
                st.clear();
            }
        }
        return ans;
    }
};
