class Solution {
public:
    string restoreString(string s, vector<int>& indices) {
        int n = s.size();
        map<int,char>mp;

        for(int i = 0;i<n;i++){
            mp[indices[i]] = s[i];
        }

        string ans = "";
        for(int i = 0;i<n;i++){
            ans.push_back(mp[i]);
        }

        return ans;
    }
};
