class Solution {
public:
    vector<string> simplifiedFractions(int n) {
        set<pair<double, string>> ans;
        for(int i = 2; i <= n;i++){
            for(int j = 1; j <= i - 1;j++){
                int g = __gcd(j, i);
                ans.insert({((j/g) * 1.0)/(i/g),to_string(j/g) + "/" + to_string(i/g)});
            }
        }
        vector<string> res;
        for(auto i : ans)res.push_back(i.second);
        return res;
    }
};
