class Solution {
public:
    string simplifyPath(string path) {
        stack<string> st;
        string temp = "";
        for(auto i : path){
            if(i == '/'){
                if(temp == "."){
                }else if(temp == ".."){
                    if(!st.empty())
                        st.pop();
                }else{
                    if(!temp.empty())
                        st.push(temp);
                }
                temp = "";
            }else{
                temp.push_back(i);
            }
        }
        if(!temp.empty()){
            if(temp == "."){
            }else if(temp == ".."){
                if(!st.empty())
                    st.pop();
            }else{
                if(!temp.empty())
                    st.push(temp);
            }
            temp = "";
        }
        stack<string> st1;
        while(!st.empty()){
            st1.push(st.top());
            st.pop();
        }
        string ans = "";
        while(!st1.empty()){
            ans.append("/" + st1.top());
            st1.pop();
        }
        if(ans.length() == 0)return "/";
        return ans;
    }
};
