class Solution {
public:
    vector<int> singleNumber(vector<int>& nums) {
        int _xor = 0;
        for(auto i : nums)
            _xor = (_xor ^ i);
        int index = -1;
        for(int i = 0 ; i < 32;i++){
            if((_xor &(1<<i))){
                index= i;
                break;
            }
        }
        int a = 0;
        int b = 0;
        for(auto i : nums){
            if((i & (1<<index))){
                a = (a ^ i);
            }else{
                b = (b ^ i);
            }
        }
        return {a, b};
    }
};
