class Solution {
public:
    vector<int> getSubarrayBeauty(vector<int>& nums, int k, int x) {
        vector<int> arr(200, 0);
        int i = 0, j = 0;
        while(j < k){
            arr[nums[j++] + 50]++;
        }
        j--;
        vector<int> ans;
        while(j < nums.size()){
            int t = 0;
            for(int i = 0; i < 200; i++){
                if(i - 50 > 0){
                    ans.push_back(0);
                    break;
                }
                if(arr[i] > 0){
                    if(t + arr[i] >= x){
                        ans.push_back(i - 50);
                        break;
                    }
                    t += arr[i];
                }
            }
            j++;
            if(j == nums.size())break;
            arr[nums[j] + 50]++;
            arr[nums[i] + 50]--;
            i++;
        }
        return ans;
    }
};
