class Solution {
public:
    vector<int> maxSlidingWindow(vector<int>& nums, int k) {
        map<int, int> m;
        for(int i = 0; i < k; i++){
            m[nums[i]]++;
        }
        vector<int> ans = {(*(m.rbegin())).first};
        int i = 0, j = k - 1;
        while(j < nums.size() - 1){
            m[nums[i]]--;
            if(m[nums[i]] == 0)m.erase(nums[i]);
            i++, j++;
            m[nums[j]]++;
            ans.push_back((*(m.rbegin())).first);
        }
        return ans;
    }
};
