class SmallestInfiniteSet {
public:
    map<int, int> mp;
    SmallestInfiniteSet() {
        for(int i = 1; i <= 1000; i++)mp[i]++;
    }

    int popSmallest() {
        int ele = mp.begin()->first;
        mp.begin()->second--;
        if(mp.begin()->second == 0)mp.erase(mp.begin());
        return ele;
    }

    void addBack(int num) {
        mp[num] = 1;
    }
};

