class Solution {
public:
    int smallestRangeII(vector<int>& a, int k) {
       int n = a.size();

        if(n == 1) {
            return 0;
        }
        sort(a.begin(), a.end());
        int ans = a[n-1] - a[0];

        for(int i=0;i<n-1;i++) {
            int x = min(a[0] + k, a[i+1] - k);
            int y  = max(a[n-1] - k, a[i] + k);
            ans = min(ans, y-x);
        }
        return ans;

    }
};
