class Solution {
public:
    string getSmallestString(int n, int k) {
        string ans;

        int len = n;
        int val = k;

        while(len) {
             char ch = 'a';
             len--;

            if(val - len <= 26) {
                ch += (val - len - 1);
                ans.push_back(ch);
                break;
            }
            else {
                ans.push_back('z');
                val -= 26;
            }
        }

        while(len--) {
            ans.push_back('a');
        }
        reverse(ans.begin(), ans.end());
        return ans;

    }
};
