class Solution {
public:

   bool isPrime(int n)
{
    // Corner cases
    if (n <= 1)
        return false;
    if (n <= 3)
        return true;

    // This is checked so that we can skip
    // middle five numbers in below loop
    if (n % 2 == 0 || n % 3 == 0)
        return false;

    for (int i = 5; i * i <= n; i = i + 6)
        if (n % i == 0 || n % (i + 2) == 0)
            return false;

    return true;
}

// Function to print primes
vector<int>  pf(int n)
{
    vector<int> res;
    for (int i = 2; i <= n; i++) {
        if (isPrime(i))
            res.push_back(i);
    }
    return res;
}
    int helper(int n,vector<int> &p){
        int sum=0;
        // cout<<n<<endl;
        if(isPrime(n)) return n;
        for(auto i:p){
            if(n<=i) return helper(sum+n,p);
            while(n!=1&&n%i==0){
                // cout<<n<<" "<<i<<endl;
                n/=i;
                sum+=i;
            }
            if(n==1) break;
        }
        return helper(sum,p);
    }
    int smallestValue(int n) {
        if(n==4) return 4;
        vector<int> p=pf(n);

        return helper(n,p);
    }
};
