long long dp[100005];
class Solution {
public:
    long long mostPoints(vector<vector<int>>& questions) {
        int n = questions.size();
        dp[n] = 0;
        for(int ind = n - 1; ind >= 0; ind--){
            long long nt = dp[ind + 1];
            long long t = dp[min(ind + questions[ind][1] + 1, n)] + questions[ind][0];
            dp[ind] = max(t, nt);
        }
        return dp[0];
    }
};
