class Solution {
public:
    vector<int> sortArray(vector<int>& nums) {
        vector<int> arr(1e5 + 10, 0);
        for(auto i : nums)arr[i + 5e4]++;
        vector<int> ans;
        for(int i = 0; i < arr.size();i++){
            while(arr[i]--){
                ans.push_back(i - 5e4);
            }
        }
        return ans;
    }
};
