class Solution {
public:
    vector<int> sortArrayByParityII(vector<int>& nums) {
        vector<int> o, e;
        for(auto i : nums)if(i%2 == 0)e.push_back(i);else o.push_back(i);
        int i = 0, j = 0;
        vector<int> arr;
        for(int k = 0; k < nums.size();k++){
            if(k%2 == 0){
                arr.push_back(e[i++]);
            }else{
                arr.push_back(o[j++]);
            }
        }
        return arr;
    }
};
