class Solution {
public:

    string frequencySort(string s) {
        map<char,int> m;
        for(char c : s){

            m[c]++;
            }
        priority_queue<pair<int,char> > q;
        for(auto i : m){
            q.push({i.second,i.first});
        }
        string ans = "";
        while(!q.empty()){
            int x = q.top().first;
            while(x){
                ans += q.top().second;
                x--;
            }
            q.pop();
        }
        return ans;

    }
};
