class Solution {
public:
    vector<int> sortEvenOdd(vector<int>& nums) {
        vector<int> o, e;
        for(int i = 0; i < nums.size();i++){
            if(i%2 == 0){
                e.push_back(nums[i]);
            }else{
                o.push_back(nums[i]);
            }
        }
        sort(e.begin(), e.end());
        sort(o.begin(), o.end(), greater<int>());
        int i = 0, j = 0;
        vector<int> arr = nums;
        for(int x = 0; x < nums.size();x++){
            if(x%2 == 0){
                arr[x] = e[i++];
            }else{
                arr[x] = o[j++];
            }
        }
        return arr;
    }
};
