class Solution {
public:
    vector<int> sortByBits(vector<int>& arr) {

        sort(arr.begin(),arr.end());
        multimap<int,int> m;
        int cnt = 0;
        for(int i=0;i<arr.size();i++){
            int x = arr[i];


            while(x){
                if(x&1 == 1) cnt++;
                x = x>>1;
            }

            m.insert({cnt,arr[i]});
            cnt = 0;
        }
        vector<int> v;

        for(auto i : m){
           v.push_back(i.second);

        }
        return v;

    }
};
