class Solution {
public:

    int steps(int n, int cnt){
        // int cnt = 0;
        if(n == 1) return cnt;
        if(n%2 == 0){

            return steps(n/2, cnt + 1);

        }
        if(n%2 == 1){
            return steps(3*n + 1, cnt + 1);

        }
        return INT_MAX;
    }
    int getKth(int lo, int hi, int k) {

        map<int, int> m;
        for(int i = lo;i<=hi;i++){
            m[i] = steps(i, 0);
        }

        priority_queue<pair<int, int> > q;
        for(auto it : m){
            q.push({it.second, it.first});
            if(q.size() > k) q.pop();
        }
        return q.top().second;




    }
};
