class Solution {
public:
    static bool cmp(pair<long long,long long> x,pair<long long,long long> y){
        return x.second<y.second;
    }
    vector<int> sortJumbled(vector<int>& m, vector<int>& nums) {
        vector<pair<long long ,long long> >umap;
        for(auto iter:nums){
            string s=to_string(iter);
            string k="";
            for(auto i:s){
                k+=to_string(m[i-'0']);
            }
            long long kk=stoll(k);
            umap.push_back({iter,kk});
        }
        sort(umap.begin(),umap.end(),cmp);
        vector<int> vec;
        for(auto iter:umap){
            vec.push_back(iter.first);
        }
        return vec;

    }
};
