class Solution {
public:
    bool isVowel(char c){
        if(c == 'a' || c == 'e' || c== 'i' || c == 'o' || c == 'u' || 
          c == 'A' || c == 'E' || c== 'I' || c == 'O' || c == 'U'){
            return true;
        }
        return false;
    }
    string sortVowels(string s) {
        vector<char> arr;
        for(auto i : s){
            if(isVowel(i)){arr.push_back(i);}
        }
        sort(arr.begin(), arr.end());
        int i = 0;
        for(auto &it : s){
            if(isVowel(it)){
                it = arr[i++];
            }
        }
        return s;
    }
};
