class Solution {
public:
    int specialArray(vector<int>& nums) {
        long long int lo = 0, hi = 1e9;
        sort(nums.begin(), nums.end());
        while(lo <= hi){
            long long int mid = (lo + hi)/2;
            int ind1 = lower_bound(nums.begin(), nums.end(), mid) - nums.begin();
            if(nums.size() - ind1 == mid){
                return mid;
            }else if(nums.size() - ind1 > mid){
                lo = mid + 1;
            }else{
                hi = mid - 1;
            }
        }
        return -1;
    }
};
