class Solution {
public:
    int balancedStringSplit(string s) {
        int cnt=0;
        int balancedstrings=0;
        for(int i=0;i<s.length();i++){
            char ch=s[i];
            if(ch=='R'){
                cnt++;
            }
            if(ch=='L'){
                cnt--;
            }
            if(cnt==0){
                balancedstrings++;
            }
        }
        return balancedstrings;
    }
};
