class Solution {
public:
    bool isPossible(vector<int>& nums) {
        unordered_map<int, int> cnt, prev;

        for(int i : nums) {
            cnt[i]++;
        }

        for(int i : nums) {
            if(cnt[i] == 0) {
                continue;
            }

            cnt[i]--;
            if(prev[i-1] > 0) {
                prev[i-1]--;
                prev[i]++;
            }
            else if(cnt[i+1] > 0 and cnt[i+2] > 0) {
                cnt[i+1]--;
                cnt[i+2]--;
                prev[i+2]++;
            } 
            else {
                return false;
            }
        }

        return true;
    }
};
