class Solution {
public:
    vector<string> splitWordsBySeparator(vector<string>& words, char separator) {
        vector<string> ans;
        for(auto x : words){
            string t = "";
            for(auto i : x){
            if(i != separator){
                t.push_back(i);
            }else{
                if(!t.empty()){
                    ans.push_back(t);
                }
                t = "";
            }
            }

        if(!t.empty()){
                    ans.push_back(t);
                }
        }
        return ans;
    }
};
