class Solution {
public:
    int splitNum(int num) {
        string s = to_string(num);
        string a = "", b = "";
        sort(s.begin(), s.end());
        for(int i = 0; i < s.length();i+=2){
            a.push_back(s[i]);
        }
        for(int i = 1; i < s.length();i+=2){
            b.push_back(s[i]);
        }
        int A = stoi(a);
        int B = stoi(b);
        return A + B;

    }
};
