class Solution {
public:
    int mySqrt(int x) {
        long long int ans = 0;
        long long int lo = 0, hi = x;
        while(lo <= hi){
            long long int mid = lo + (hi - lo)/2;
            if(mid * mid >= x){
                ans = mid;
                hi = mid - 1;
            }else{
                lo = mid + 1;
            }
        }
        while(ans * ans > x)ans--;
        return ans;
    }
};
