class Solution {
public:
    vector<int> sortedSquares(vector<int>& nums) {
        vector<int> arr(10001, 0);
        for(auto i : nums){
            arr[abs(i)]++;
        }
        vector<int> ans;
        for(int i = 0; i < 10001;i++){
            for(int j = 0; j < arr[i];j++){
                ans.push_back(i * i);
            }
        }
        return ans;
    }
};
