class Solution {
public:


    unordered_map<int,bool> umap;
    bool decide(int n){
        if(n<=0) return false;

        int key = n;
        if(umap.count(key)) return umap[key];

        bool ans;
       for (int k = 1; k * k <= n; k++) {
            ans=decide(n-k*k);
             if(ans==false) {
                  return umap[key]=true;
             }
       }
        return umap[key]=false;
    }
    bool winnerSquareGame(int n) {
        return decide(n);
    }
};
